pragma solidity ^0.8.0;

// SPDX-License-Identifier: MIT

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Voting {
    IERC20 public joshcoin;
    uint256 public minimumTokensToVote = 1 * 10**18; // 1 Joshcoin (assuming 18 decimals)
    uint256 public votingDuration = 48 hours;
    uint256 public passingPercentage = 60;

    enum ProposalStatus { Open, Closed, Executed }

    struct Proposal {
        string description;
        uint256 yesVotes;
        uint256 noVotes;
        uint256 endTime;
        ProposalStatus status;
        mapping(address => bool) hasVoted;
        mapping(address => string) voteReasons;
    }

    Proposal[] public proposals;

    constructor(address _joshcoin) {
        joshcoin = IERC20(_joshcoin);
    }

    function createProposal(string memory _description) public {
        Proposal storage newProposal = proposals.push();
        newProposal.description = _description;
        newProposal.yesVotes = 0;
        newProposal.noVotes = 0;
        newProposal.endTime = block.timestamp + votingDuration;
        newProposal.status = ProposalStatus.Open;
    }

    function vote(uint256 _proposalId, bool _vote, string memory _reason) public {
        require(_proposalId < proposals.length, "Invalid proposal ID");
        require(joshcoin.balanceOf(msg.sender) >= minimumTokensToVote, "Not enough Joshcoin to vote");
        require(!proposals[_proposalId].hasVoted[msg.sender], "Already voted on this proposal");
        require(proposals[_proposalId].status == ProposalStatus.Open, "Proposal voting is closed");
        require(block.timestamp <= proposals[_proposalId].endTime, "Voting period has ended");

        if (_vote) {
            proposals[_proposalId].yesVotes++;
        } else {
            proposals[_proposalId].noVotes++;
        }
        proposals[_proposalId].hasVoted[msg.sender] = true;
        proposals[_proposalId].voteReasons[msg.sender] = _reason;

        // Close the proposal if the voting period has ended
        if (block.timestamp >= proposals[_proposalId].endTime) {
            proposals[_proposalId].status = ProposalStatus.Closed;
        }
    }

    function isProposalPassed(uint256 _proposalId) public view returns (bool) {
        require(_proposalId < proposals.length, "Invalid proposal ID");
        Proposal storage proposal = proposals[_proposalId];
        uint256 totalVotes = proposal.yesVotes + proposal.noVotes;
        uint256 yesVotePercentage = (proposal.yesVotes * 100) / totalVotes;
        return yesVotePercentage >= passingPercentage;
    }
}

